$(function () {



    function details(event) {
        event.preventDefault();

        const courseTitle = $(event.currentTarget).text().replace(/\s{2,}/,' ').trim();
        const courseData = popupData[courseTitle];
        const $modalBody = $('#dlgDetails .modal-body span');

        if (courseData && $modalBody) {
            const $name = $modalBody[0];
            const $code = $modalBody[1];
            const $units = $modalBody[2];
            const $description = $modalBody[3];

            $name.innerText = courseData.name;
            $code.innerText = courseData.code;
            $units.innerText = courseData.units;
            $description.innerText = courseData.description;

            $('#dlgDetails').modal('show');
        } else {
            console.warn('no course info for', courseTitle);
        }
    }

    function print() {
        $(".WGUHeader").addClass("no-print");
        $(".globalNav").addClass("no-print");
        $("#suiteBar").addClass("no-print");
        $('#printDisclaimer').show();
        //var footerDiv = $('#s4-workspace > footer')[0].innerHTML;
        //document.body.innerHTML += footerDiv;
        window.print();
    }


    $('#content table a.ng-binding').on('click', details);
    $('#print_btn').on('click', print);

})
